"use strict";
/** event handler registration object */
Vanil.events = {
    __warnNonInteractive: (eventName) => console.warn(`Vanil.events.${eventName} not registered. Element remains non-interactive.`),
};
Vanil.e = (eventHandlerName, event) => {
    if (Vanil.events[eventHandlerName]) {
        Vanil.events[eventHandlerName](event);
    }
    else {
        Vanil.events.__warnNonInteractive(eventHandlerName);
    }
};
Vanil.on = function (eventHandlerName, handlerFn) {
    Vanil.events[eventHandlerName] = handlerFn;
};
